def preprocess(data_list):
    h = html2text.HTML2Text()
    h.ignore_links = True
    h.ignore_images = True
    h.ignore_emphasis = True
    for entry in data_list:
        content = h.handle(entry["content"] )
        content = content.replace(" ", "")
        content = content.replace(" ", "")
        entry["content"] = content.replace("\n", "")


